(551, 1, '2014-08-14 16:48:13', '2014-08-14 20:48:13', '/*\nWelcome to Custom CSS!\n\nCSS (Cascading Style Sheets) is a kind of code that tells the browser how\nto render a web page. You may delete these comments and get started with\nyour customizations.\n\nBy default, your stylesheet will be loaded after the theme stylesheets,\nwhich means that your rules can take precedence and override the theme CSS\nrules. Just write here what you want to change, you don\'t need to copy all\nyour theme\'s stylesheet content.\n*/\n/* Set logo to extend below the main navigation bar \n#logo img {\n	max-height: none !important;\n	height: 130px !important;\n} */\n/* Fix logo and menu icon on mobile pages \n#show-nav-menu {\n	float: left !important;\n	color: white !important;\n}\n\n@media handheld,only screen and (max-width:900px) {\n	#logo img {\n		max-height: 100% !important;\n	}\n} */\n/* Reduce space around logo \n#logo img, #header-inner.stuck #logo img {\n	max-height: 80% !important;\n} */\n/* Custom style for credits on individual episode pages */\n.credits {\n	font-size: smaller !important;\n	font-weight: 900 !important;\n}\n\n/* Extra large type style */\n.x-large {\n	font-size: x-large;\n	font-weight: 900 !important;\n	border-width: 4px !important;\n	border-color: black !important;\n}\n\n/* Resize and bold the gallery item name type */\n.hb-gallery-item-name {\n	font-size: 9pt !important;\n	font-weight: 900 !important;\n	border: none !important;\n	padding: 0;\n}\n\n.hb-gal-standard-count {\n	visibility: hidden !important;\n}\n\n.hb-gal-standard-description {\n	border: none !important;\n	padding-top: 20px !important;\n}\n\n.noborder {\n	border: none;\n	padding: 0;\n}\n\n/* Product amount red text */\n.amount {\n	color: red !important;\n}\n\n.special {\n	color: white !important;\n}\n\n/* Attempt to make a blank page - No header, no footer */\n.page-id-99 #hb-header {\n	display: none;\n}\n\n.page-id-99 #copyright-wrapper {\n	display: none;\n}\n\n.page-id-99 #contact-button {\n	display: none;\n}\n\n.whiteText {\n	color: white !important;\n}\n\ndiv#whiteText h1 {\n	color: white !important;\n}\n\ndiv#whiteText p {\n	color: white !important;\n}\n\n.episodeHead {\n	font-size: x-large;\n	font-weight: 900 !important;\n	border-bottom: 1px solid red;\n	padding-bottom: 5px;\n}\n\n.episodeHeadWhiteText {\n	font-size: xx-large;\n	color: white;\n	font-weight: 900 !important;\n	text-transform: uppercase;\n	border-bottom: 1px solid red;\n	padding-bottom: 5px;\n}\n\n/* Fix hover issue on overlays and ajax buttons */\na.load-more-posts:hover {\n	color: #FFFFFF !important;\n}\n\na.hb-buy-button {\n	color: #FFFFFF !important;\n}\n\na.hb-buy-button:hover {\n	color: #DD3333 !important;\n}\n\na.hb-more-details {\n	color: #FFFFFF !important;\n}\n\na.hb-more-details:hover {\n	color: #DD3333 !important;\n}\n\n/* Change widget title to U&lc, bold, add underline and negative space below */\nh4.widget-title {\n	font-size: 20pt !important;\n	text-transform: capitalize !important;\n	font-weight: bold !important;\n	margin-bottom: 5px !important;\n	border-bottom-style: solid;\n	border-width: 2px;\n	padding-bottom: 5px;\n}\n\n/* Change product widget title to U&lc and bold */\n.widget-item.woocommerce .product_list_widget a {\n	font-size: 11pt;\n	padding-top: 10px;\n	font-weight: 700 !important;\n	text-transform: capitalize !important;\n}\n\n/* Add space above news widget elements */\n.cat-post-item {\n	padding-top: 10px !important;\n	padding-bottom: 10px !important;\n}\n\n/* pf and p indent rule and remove space above \np+p {\n	text-indent: 2em;\n} */\n/* Remove space after p \np {\n	margin: 0;\n	padding: 0;\n} */', 'safecss', 'Highend Child', 'inherit', 'open', 'open', '', '456-revision-v1', '', '', '2014-08-14 16:48:13', '2014-08-14 20:48:13', '', 456, 'http://dev.genco.tv/2014/08/456-revision-v1/', 0, 'revision', '', 0),
