(681, 1, '2014-08-21 11:02:45', '2014-08-21 15:02:45', '@charset  "UTF-8";\n\n/*\nWelcome to Custom CSS!\n\nCSS (Cascading Style Sheets) is a kind of code that tells the browser how\nto render a web page. You may delete these comments and get started with\nyour customizations.\n\nBy default, your stylesheet will be loaded after the theme stylesheets,\nwhich means that your rules can take precedence and override the theme CSS\nrules. Just write here what you want to change, you don\'t need to copy all\nyour theme\'s stylesheet content.\n*/\n/* Set logo to extend below the main navigation bar \n#logo img {\n	max-height: none !important;\n	height: 130px !important;\n} */\n/* Fix logo and menu icon on mobile pages \n#show-nav-menu {\n	float: left !important;\n	color: white !important;\n}\n\n@media handheld,only screen and (max-width:900px) {\n	#logo img {\n		max-height: 100% !important;\n	}\n} */\n/* Reduce space around logo \n#logo img, #header-inner.stuck #logo img {\n	max-height: 80% !important;\n} */\n/*.row.fw-content-wrap {\n	background: rgba(0,0,0,.5) !important;\n	border-style: solid;\n	border-width: 100px;\n	border-color: rgba(0,0,0,.5) !important;\n}*/\n#showbizHead a {\n	font-family: "Arvo", sans-serif;\n	font-size: 13px;\n	color: #FFFFFF !important;\n	line-height: 22px;\n	letter-spacing: 0;\n	font-weight: normal;\n}\n\n.blog-shortcode-1 .element-row {\n	margin-bottom: -20px !important;\n}\n\n.blog-shortcode-1 .blog-list-item-date {\n	display: none;\n}\n\n/* Custom style for credits on individual episode pages */\n.credits {\n	font-size: smaller !important;\n	font-weight: 900 !important;\n}\n\n/* Extra large type style */\n.x-large {\n	font-size: x-large;\n	font-weight: 900 !important;\n	border-width: 4px !important;\n	border-color: black !important;\n}\n\n/* Resize and bold the gallery item name type */\n.hb-gallery-item-name {\n	font-size: 9pt !important;\n	font-weight: 900 !important;\n	border: none !important;\n	padding: 0;\n}\n\n.hb-gal-standard-count {\n	visibility: hidden !important;\n}\n\n.hb-gal-standard-description {\n	border: none !important;\n	padding-top: 20px !important;\n}\n\n.noborder {\n	border: none;\n	padding: 0;\n}\n\n/* Product amount red text */\n.amount {\n	color: red !important;\n}\n\n.special {\n	color: white !important;\n}\n\n.episodeHead {\n	font-size: x-large;\n	font-weight: 900 !important;\n	border-bottom: 1px solid red;\n	padding-bottom: 5px;\n}\n\n.episodeHeadWhiteText {\n	font-size: xx-large;\n	color: white;\n	font-weight: 900 !important;\n	text-transform: uppercase;\n	border-bottom: 1px solid red;\n	padding-bottom: 5px;\n}\n\n/* Fix hover issue on overlays and ajax buttons */\na.previous {\n	color: #FFFFFF !important;\n}\n\na.next {\n	color: #FFFFFF !important;\n}\n\na.load-more-posts:hover {\n	color: #FFFFFF !important;\n}\n\na.hb-buy-button {\n	color: #FFFFFF !important;\n}\n\na.hb-buy-button:hover {\n	color: #DD3333 !important;\n}\n\na.hb-more-details {\n	color: #FFFFFF !important;\n}\n\na.hb-more-details:hover {\n	color: #DD3333 !important;\n}\n\n/* Change widget title to U&lc, bold, add underline and negative space below */\nh4.widget-title {\n	font-size: 20pt !important;\n	text-transform: capitalize !important;\n	font-weight: bold !important;\n	margin-bottom: 5px !important;\n	border-bottom-style: solid;\n	border-width: 2px;\n	padding-bottom: 5px;\n}\n\n/* Change product widget title to U&lc and bold */\n.widget-item.woocommerce .product_list_widget a {\n	font-size: 11pt;\n	padding-top: 10px;\n	font-weight: 700 !important;\n	text-transform: capitalize !important;\n}\n\n/* Add space above news widget elements */\n.cat-post-item {\n	padding-top: 10px !important;\n	padding-bottom: 10px !important;\n}\n\n/* Remove text-shadow from navigation buttons */\n#showbiz_blogPosts_1  .showbiz-navigation i {\n	text-shadow: 0 0 0 !important;\n}\n\n/******************************\n	-	THE FONTELLO ICONS	-\n********************************/\n@font-face {\n	font-family: FontAwesome;\n	src: url(fontawesome/fontawesome-webfont.svg#FontAwesome) format("svg");\n	src: url(fontawesome/fontawesome-webfont.eot);\n	src: url(fontawesome/fontawesome-webfont.eot?#iefix) format(\'embedded-opentype\'), url(fontawesome/fontawesome-webfont.woff) format(\'woff\'), url(fontawesome/fontawesome-webfont) format("truetype");\n	font-weight: 400;\n	font-style: normal;\n}\n\n[class^="new-icon-"]:before,\n	[class*="new-icon-"]:before {\n	font-family: FontAwesome;\n	font-weight: 400;\n	font-style: normal;\n	text-decoration: none !important;\n	-webkit-font-smoothing: antialiased;\n	margin-right: .3em;\n	display: inline;\n	width: auto;\n	height: autopx;\n	line-height: inherit;\n	vertical-align: middle;\n	background-image: none;\n	background-position: 0 0;\n	background-repeat: repeat;\n	margin-top: 0;\n}\n\n.new-icon-plus:before {\n	content: \'\\e800\';\n}\n\n/* \'?\' */\n.new-icon-minus:before {\n	content: \'\\e801\';\n}\n\n/* \'?\' */\n.new-icon-pause:before {\n	content: \'\\e802\';\n}\n\n/* \'?\' */\n.new-icon-stop:before {\n	content: \'\\e803\';\n}\n\n/* \'?\' */\n.new-icon-play:before {\n	content: \'\\e804\';\n}\n\n/* \'?\' */\n.new-icon-star:before {\n	content: \'\\e816\';\n}\n\n/* \'?\' */\n.new-icon-star-empty:before {\n	content: \'\\e817\';\n}\n\n/* \'?\' */\n.new-icon-cancel:before {\n	content: \'\\e805\';\n}\n\n/* \'?\' */\n.new-icon-doc-landscape:before {\n	content: \'\\e806\';\n}\n\n/* \'?\' */\n.new-icon-down-open:before {\n	content: \'\\e807\';\n}\n\n/* \'?\' */\n.new-icon-left-open:before {\n	margin-right: .3em;\n	content: \'\\f104\';\n}\n\n/* \'?\' */\n.new-icon-right-open:before {\n	margin-left: .4em;\n	content: \'\\f105\';\n}\n\n/* \'?\' */\n.new-icon-up-open:before {\n	content: \'\\e80e\';\n}\n\n/* \'?\' */\n.new-icon-down-open-mini:before {\n	content: \'\\e811\';\n}\n\n/* \'?\' */\n.new-icon-left-open-mini:before {\n	content: \'\\e80a\';\n}\n\n/* \'?\' */\n.new-icon-right-open-mini:before {\n	content: \'\\e80c\';\n}\n\n/* \'?\' */\n.new-icon-up-open-mini:before {\n	cont