n	visibility: hidden !important;\n}\n\n.hb-gal-standard-description {\n	border: none !important;\n	padding-top: 20px !important;\n}\n\n.noborder {\n	border: none;\n	padding: 0;\n}\n\n/* Product amount red text */\n.amount {\n	color: red !important;\n}\n\n.special {\n	color: white !important;\n}\n\n.episodeHead {\n	font-size: x-large;\n	font-weight: 900 !important;\n	border-bottom: 1px solid red;\n	padding-bottom: 5px;\n}\n\n.episodeHeadWhiteText {\n	font-size: xx-large;\n	color: white;\n	font-weight: 900 !important;\n	text-transform: uppercase;\n	border-bottom: 1px solid red;\n	padding-bottom: 5px;\n}\n\n/* Fix hover issue on overlays and ajax buttons */\na.previous {\n	color: #FFFFFF !important;\n}\n\na.next {\n	color: #FFFFFF !important;\n}\n\na.load-more-posts:hover {\n	color: #FFFFFF !important;\n}\n\na.hb-buy-button {\n	color: #FFFFFF !important;\n}\n\na.hb-buy-button:hover {\n	color: #DD3333 !important;\n}\n\na.hb-more-details {\n	color: #FFFFFF !important;\n}\n\na.hb-more-details:hover {\n	color: #DD3333 !important;\n}\n\n/* Change widget title to U&lc, bold, add underline and negative space below */\nh4.widget-title {\n	font-size: 20pt !important;\n	text-transform: capitalize !important;\n	font-weight: bold !important;\n	margin-bottom: 5px !important;\n	border-bottom-style: solid;\n	border-width: 2px;\n	padding-bottom: 5px;\n}\n\n/* Change product widget title to U&lc and bold */\n.widget-item.woocommerce .product_list_widget a {\n	font-size: 11pt;\n	padding-top: 10px;\n	font-weight: 700 !important;\n	text-transform: capitalize !important;\n}\n\n/* Add space above news widget elements */\n.cat-post-item {\n	padding-top: 10px !important;\n	padding-bottom: 10px !important;\n}\n\n/* Remove text-shadow from navigation buttons */\n#showbiz_blogPosts_1  .showbiz-navigation i {\n	text-shadow: 0 0 0 !important;\n}\n\n/******************************\n	-	THE FONTELLO ICONS	-\n********************************/\n@font-face {\n	font-family: FontAwesome;\n	src: url(fontawesome/fontawesome-webfont.svg#FontAwesome) format("svg");\n	src: url(fontawesome/fontawesome-webfont.eot);\n	src: url(fontawesome/fontawesome-webfont.eot?#iefix) format(\'embedded-opentype\'), url(fontawesome/fontawesome-webfont.woff) format(\'woff\'), url(fontawesome/fontawesome-webfont) format("truetype");\n	font-weight: 400;\n	font-style: normal;\n}\n\n[class^="new-icon-"]:before,\n	[class*="new-icon-"]:before {\n	font-family: FontAwesome;\n	font-weight: 400;\n	font-style: normal;\n	text-decoration: none !important;\n	-webkit-font-smoothing: antialiased;\n	margin-right: .3em;\n	display: inline;\n	width: auto;\n	height: autopx;\n	line-height: inherit;\n	vertical-align: middle;\n	background-image: none;\n	background-position: 0 0;\n	background-repeat: repeat;\n	margin-top: 0;\n}\n\n.new-icon-plus:before {\n	content: \'\\e800\';\n}\n\n/* \'?\' */\n.new-icon-minus:before {\n	content: \'\\e801\';\n}\n\n/* \'?\' */\n.new-icon-pause:before {\n	content: \'\\e802\';\n}\n\n/* \'?\' */\n.new-icon-stop:before {\n	content: \'\\e803\';\n}\n\n/* \'?\' */\n.new-icon-play:before {\n	content: \'\\e804\';\n}\n\n/* \'?\' */\n.new-icon-star:before {\n	content: \'\\e816\';\n}\n\n/* \'?\' */\n.new-icon-star-empty:before {\n	content: \'\\e817\';\n}\n\n/* \'?\' */\n.new-icon-cancel:before {\n	content: \'\\e805\';\n}\n\n/* \'?\' */\n.new-icon-doc-landscape:before {\n	content: \'\\e806\';\n}\n\n/* \'?\' */\n.new-icon-down-open:before {\n	content: \'\\e807\';\n}\n\n/* \'?\' */\n.new-icon-left-open:before {\n	margin-right: .3em;\n	content: \'\\f104\';\n}\n\n/* \'?\' */\n.new-icon-right-open:before {\n	margin-left: .4em;\n	content: \'\\f105\';\n}\n\n/* \'?\' */\n.new-icon-up-open:before {\n	content: \'\\e80e\';\n}\n\n/* \'?\' */\n.new-icon-down-open-mini:before {\n	content: \'\\e811\';\n}\n\n/* \'?\' */\n.new-icon-left-open-mini:before {\n	content: \'\\e80a\';\n}\n\n/* \'?\' */\n.new-icon-right-open-mini:before {\n	content: \'\\e80c\';\n}\n\n/* \'?\' */\n.new-icon-up-open-mini:before {\n	content: \'\\e80f\';\n}\n\n/* \'?\' */\n.new-icon-down-open-big:before {\n	content: \'\\e812\';\n}\n\n/* \'?\' */\n.new-icon-left-open-big:before {\n	content: \'\\e809\';\n}\n\n/* \'?\' */\n.new-icon-right-open-big:before {\n	content: \'\\e80d\';\n}\n\n/* \'?\' */\n.new-icon-up-open-big:before {\n	content: \'\\e810\';\n}\n\n/* \'?\' */\n.new-icon-mobile:before {\n	content: \'\\e813\';\n}\n\n/* \'?\' */\n.new-icon-search:before {\n	content: \'\\e814\';\n}\n\n/* \'?\' */\n.new-icon-link:before {\n	content: \'\\e815\';\n}\n\n/* \'?\' */\n/*********************************************************\n			-	NAVIGATION SETTINGS	-\n	USE STYLE CLASS IN THE SHOWBIZ NAVIGATION CONTAINER\n**********************************************************/\n.showbiz-navigation {\n}\n\n.showbiz-navigation.center {\n	text-align: center;\n}\n\n/* My replacement class */\n.new-navigation {\n}\n\n.new-navigation.center {\n	text-align: center;\n}\n\n.sb-navigation-left,\n		.sb-navigation-right,\n		.sb-navigation-play {\n	cursor: pointer;\n	border-radius: 0;\n	-moz-border-radius: 3px;\n	-webkit-border-radius: 3px;\n	display: inline-block;\n	padding: 0 !important;\n	-webkit-transition: all .3s ease;\n	-moz-transition: all .3s ease-in-out;\n	-o-transition: all .3s ease-in-out;\n	width: 35px;\n	height: 35px;\n	line-height: 33px;\n	-ms-transition: all .3s ease-in-out;\n	transition: all .3s ease-in-out;\n}\n\n.sb-navigation-play {\n	margin-right: 5px;\n}\n\n.sb-navigation-left {\n	margin-right: 5px;\n}\n\n.sb-navigation-left i,\n		.sb-navigation-right i,\n		.sb-navigation-play i {\n	font-size: 22px;\n	color: #fff;\n}\n\n.sb-navigation-left.notclickable {\n	cursor: default;\n	-moz-opacity: .3;\n	filter: alpha(opacity=30);\n	opacity: .3;\n}\n\n.sb-navigation-right.notclickable {\n	cursor: default;\n	-moz-opacity: .3;\n	filter: alpha(opacity=30);\n	opacity: .3;\n}\n\n.sb-hidden {\n	display: none;\n}', 'safecss', 'Highend Child', 'inherit', 'open', 'open', '', '456-revision-v1', '', '', '2014-08-21 11:03:50',